// Home.js

import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

const Home = () => {
  const [accounts, setAccounts] = useState([
    "GShiMwUiqpWfxyVxVnYo96upwQekeFVNrtMQT4aSH2RT",
    "CRVidEDtEUTYZisCxBZkpELzhQc9eauMLR3FWg74tReL",
  ]);
  // 后续改成接入数据库读取
  const [balances, setBalances] = useState({});
  const [newAddress, setNewAddress] = useState('');

  const navigate = useNavigate();
  const wsRef = useRef(null);

  // 从后端获取初始余额
  useEffect(() => {
    const fetchBalances = async () => {
      try {
        for (const account of accounts) {
          const solResponse = await fetch(`/solBalance/${account}`);
          const solData = await solResponse.json();

          setBalances(prev => ({ ...prev, [account]: solData.balance }));
        }
      } catch (error) {
        console.error('取得初始余额时出错:', error);
      }
    };

    fetchBalances();
  }, [accounts]);

  // 初始化 WebSocket 连接
  useEffect(() => {
    wsRef.current = new WebSocket('ws://localhost:5001/ws');

    wsRef.current.onopen = () => {
      console.log('WebSocket 已连接');

      // 订阅所有账户
      accounts.forEach((account) => {
        wsRef.current.send(JSON.stringify({ action: 'subscribe', account }));
      });
    };

    wsRef.current.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('收到消息:', data);

      if (data.type === 'solBalance') {
        // 更新对应账户的余额
        setBalances(prev => ({ ...prev, [data.account]: data.balance }));
        console.log(`账户 ${data.account} 的 SOL 余额更新:`, data.balance);
      } else if (data.type === 'splBalances') {
        // 如果需要处理 SPL 代币余额，可以在这里处理
        console.log(`账户 ${data.account} 的 SPL 代币余额更新:`, data.balances);
      } else if (data.type === 'heartbeat') {
        console.log('收到心跳消息');
      }
    };

    wsRef.current.onclose = () => {
      console.log('WebSocket 已断开');
    };

    return () => {
      wsRef.current.close();
    };
  }, [accounts]); // 当 accounts 变化时，重新建立 WebSocket 连接并订阅

  // 处理新增钱包地址
  const handleAddAccount = () => {
    if (newAddress && !accounts.includes(newAddress)) {
      setAccounts([...accounts, newAddress]);
      setNewAddress('');
    }
  };

  return (
    <div className="home-container">
      {/* 左侧：钱包总览和新增地址 */}
      <div className="left-side">
        <div className="add-account">
          <input
            type="text"
            placeholder="输入新的钱包地址"
            value={newAddress}
            onChange={(e) => setNewAddress(e.target.value)}
          />
          <button onClick={handleAddAccount}>新增钱包地址</button>
        </div>

        <div className="wallet-overview">
          {accounts.map(account => (
            <div key={account} className="wallet-item">
              {/* 点击钱包地址，导航到详细页面 */}
              <h3
                onClick={() => navigate(`/wallet/${account}`)}
                style={{ cursor: 'pointer' }}
              >
                {account}
              </h3>
              <p>余额: {balances[account] !== undefined ? balances[account] : '加载中...'} $SOL</p>
            </div>
          ))}
        </div>
      </div>

      {/* 右侧：买入代币功能区域 */}
      <div className="right-side">
        <h3>买入代币功能</h3>
        {/* 在此处添加您的买入代币相关组件或代码 */}
      </div>
    </div>
  );
};

export default Home;
