// 导入 React 和必要的钩子函数
import React, { useState, useCallback } from 'react';
// 导入样式表
import './App.css';
// 导入子组件 WebSocketComponent
import WebSocketComponent from './WebSocketComponent';

// 定义 App 组件
const App = () => {
  // 定义消息列表的連接result數值，初始为空数组
  const [connectionResult, setConnectionResult] = useState("None");
  // 定义连接状态的状态变量，初始值为 "Disconnected"
  const [connectionStatus, setConnectionStatus] = useState("Disconnected");
  // 定义消息列表的状态变量，初始为空数组
  const [messages, setMessages] = useState([]);
  // 需要订阅的账户地址
  const account = "GShiMwUiqpWfxyVxVnYo96upwQekeFVNrtMQT4aSH2RT";

  // 处理连接状态更新的回调函数
  const handleConnectionStatus = useCallback((status) => {
    setConnectionStatus(status);
  }, []);
  // 处理新消息的Result
  const handleConnectionResult = useCallback((status) => {
    setConnectionResult(status);
  }, []);
  // 处理新消息的回调函数
  const handleNewMessage = useCallback((message) => {
    setMessages(prevMessages => [...prevMessages, message]); // 更新消息状态
  }, []);

  // 渲染组件
  return (
    <div className="App">
      <header className="App-header">
        {/* 显示 WebSocket 的连接状态 */}
        <h2>WebSocket Status: {connectionStatus}</h2>
      </header> 
      {/* 引入 WebSocketComponent，并传递所需的 props */}
      <WebSocketComponent 
        onConnectionStatus={handleConnectionStatus} 
        account={account} 
        onMessageReceived={handleNewMessage} // 传递回调函数
        onConnectionResult={handleConnectionResult}
      />
      <div className='dashboard'>
        <h2>{account}:</h2>
        
        {/* 顯示最新的 result 值 */}
        <h4>Latest Result: {connectionResult}</h4>

        <h3>Received Messages:</h3>
        <ul>
          {/* 显示接收到的消息列表 */}
          {messages.map((msg, index) => (
            <li key={index}>{msg}</li> // 显示每条消息
          ))}
        </ul>
      </div>
    </div>
  );
}

// 导出 App 组件
export default App;
