// 导入 React 及其必要的钩子函数
import React, { useState, useCallback } from 'react';
// 导入样式表
import './App.css';
// 导入自定义的 WebSocket 组件
import WebSocketComponent from './WebSocketComponent';
// 导入 buffer 包并在浏览器环境中 polyfill Buffer
import { Buffer } from 'buffer';
window.Buffer = Buffer;

// 定义主组件 App
const App = () => {
  // 定义状态变量，用于存储连接结果，初始值为 "None"
  const [connectionResult, setConnectionResult] = useState("None");

  // 定义状态变量，用于存储连接状态，初始值为 "Disconnected"
  const [connectionStatus, setConnectionStatus] = useState("Disconnected");

  // 定义状态变量，用于存储接收到的消息，初始为空数组
  const [messages, setMessages] = useState([]);

  // 定义状态变量，用于存储 SPL 代币余额，初始为空数组
  const [splBalances, setSplBalances] = useState([]);
  const [previousBalance, setPreviousBalance] = useState(null); // 上一個餘額
  const [solBalance, setSolBalance] = useState(null); // 當前 SOL 餘額
  const [percentageChange, setPercentageChange] = useState(null); // 漲跌百分比
  // 要订阅的账户地址
  const account = "GShiMwUiqpWfxyVxVnYo96upwQekeFVNrtMQT4aSH2RT"

  // 使用 useCallback 定义处理连接状态更新的函数，避免不必要的重新渲染
  const handleConnectionStatus = useCallback((status) => {
    setConnectionStatus(status);
  }, []);

  // 定义处理连接结果更新的函数
  const handleConnectionResult = useCallback((status) => {
    setConnectionResult(status);
  }, []);

  // 定义处理新消息的函数，更新消息列表
  const handleNewMessage = useCallback((message) => {
    const newBalance = parseFloat(message.split("餘額: ")[1].split(" $SOL")[0]); // 取得新餘額

    if (previousBalance !== null) {
      const change = ((newBalance - previousBalance) / previousBalance) * 100;
      setPercentageChange(change.toFixed(2));
    }

    setPreviousBalance(newBalance);
    setSolBalance(newBalance);
  }, [previousBalance]);

  // 定义处理 SPL 代币余额更新的函数，解析 JSON 字符串并存储为对象数组
  const handleSPLbalance = useCallback((balances) => {
    try {
      const parsedBalances = JSON.parse(balances);

      // 过滤掉 balance 为 0 的代币
      const nonZeroBalances = parsedBalances.filter(token => parseFloat(token.balance) > 0);

      setSplBalances(nonZeroBalances);
    } catch (error) {
      console.error('解析 SPL 代币余额时出错:', error);
    }
  }, []);

  // 返回要渲染的 JSX 元素
  return (
    <div className="App">
      <header className="App-header">
        {/* 显示 WebSocket 的连接状态 */}
        <h2>WebSocket 狀態: {connectionStatus}</h2>
      </header>
      {/* 引入 WebSocketComponent 组件，并传递必要的属性 */}
      <WebSocketComponent 
        onConnectionStatus={handleConnectionStatus} 
        account={account} 
        onMessageReceived={handleNewMessage} 
        onConnectionResult={handleConnectionResult}
        onSPLbalance={handleSPLbalance}
      />
      {/* 主体内容 */}
      <div className='dashboard-co'>
        {/* 显示账户地址 */}
        <h2>{account}:</h2>
        {/* 显示最新的连接结果 */}
        <h4> {connectionResult}</h4>
        <h3>接收到的消息:</h3>
        <div className='dashboard-bottom-container'>
          {/* 左侧消息列表 */}
          <div className='dashboard_left'>
            <h3>Balance: {solBalance} $SOL</h3>
            {percentageChange !== null && (
              <h4 style={{ color: percentageChange > 0 ? 'green' : 'red' }}>
                漲跌: {percentageChange}%
              </h4>
            )}
          </div>
          {/* 右侧 SPL 代币余额列表 */}
          <div className='dashboard_right'>
            <div className="token-grid">
              {splBalances.map((token, index) => (
                <div key={index} className="token-item" onClick={() => navigator.clipboard.writeText(token.mint)}>
                  <p>TOKEN 合約地址: {token.mint.slice(0, 7)}...{token.mint.slice(-7)}</p>
                  <p>錢包餘額: {parseFloat(token.balance).toFixed(4)}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// 导出 App 组件
export default App;
