// 导入 React 和必要的钩子函数
import React, { useEffect } from 'react';
import { Connection, PublicKey } from '@solana/web3.js';

const WebSocketComponent = React.memo(({ onConnectionStatus, account, onMessageReceived, onConnectionResult }) => {

  useEffect(() => {
    // 创建 Solana 连接，指定 HTTP 和 WebSocket 端点
    const connection = new Connection(
      'https://api.mainnet-beta.solana.com', // 您可以替换为您的自定义 RPC 端点
      {
        wsEndpoint: 'wss://api.mainnet-beta.solana.com', // 您可以替换为您的自定义 WebSocket 端点
        commitment: 'confirmed', // 设置承诺级别
      }
    );

    // 更新连接状态为 Connected
    onConnectionStatus('Connected');

    // 将账户地址转换为 PublicKey 对象
    const publicKey = new PublicKey(account);

    // 订阅账户变化
    const subscriptionId = connection.onAccountChange(
      publicKey,
      (accountInfo, context) => {
        // 解析账户信息
        const lamports = accountInfo.lamports;
        console.log('Updated account info:', accountInfo);
        console.log('Slot:', context.slot);

        // 调用回调函数传递余额信息
        if (lamports !== undefined) {
          onMessageReceived(`余额: ${lamports}`);
        }

        // 传递 slot 信息
        onConnectionResult(`Slot: ${context.slot}`);
      },
      'confirmed'
    );

    // 错误处理
    connection._rpcWebSocket.on('error', (error) => {
      console.error('Connection error:', error);
      onConnectionStatus('Error');
    });

    // 组件卸载时取消订阅
    return () => {
      connection.removeAccountChangeListener(subscriptionId)
        .then(() => {
          console.log('Account change listener removed.');
          onConnectionStatus('Disconnected');
        })
        .catch((error) => {
          console.error('Error removing account change listener:', error);
        });
    };
  }, [onConnectionStatus, account, onMessageReceived, onConnectionResult]);

  // 该组件不渲染任何 UI
  return null;
});

export default WebSocketComponent;
