import React, { useEffect, useRef } from 'react';
import { Connection, PublicKey } from '@solana/web3.js';

// 查询指定钱包的所有 SPL Token 余额
async function fetchAllSplTokenBalances(connection, walletAddress) {
  try {
    // 获取钱包地址下所有 SPL Token 账户
    const response = await connection.getTokenAccountsByOwner(
      new PublicKey(walletAddress), // 钱包地址
      {
        programId: new PublicKey("TokenkegQfeZyiNwAJbNbGKPFXCWuBvf9Ss623VQ5DA"), // SPL Token 程序 ID
      },
      {
        encoding: "jsonParsed" // 解析数据为 JSON 格式，方便读取
      }
    );

    // 处理返回的账户数据，提取每个 Token 的余额信息
    const tokenBalances = response.value.map((accountInfo) => {
      const { mint, tokenAmount } = accountInfo.account.data.parsed.info;
      return {
        mint, // Token 的 Mint 地址
        balance: tokenAmount.uiAmountString // 使用小数点表示的余额
      };
    });

    return tokenBalances;

  } catch (error) {
    console.error("获取 SPL Token 余额时出错:", error);
    throw error;
  }
}






const WebSocketComponent = React.memo(({ onConnectionStatus, account, onMessageReceived, onConnectionResult,onSPLbalance }) => {
  const hasEffectRun = useRef(false);
  const connectionRef = useRef(null);
  const subscriptionIdRef = useRef(null);

  useEffect(() => {
    if (hasEffectRun.current) return;
    hasEffectRun.current = true;

    const RPC_ENDPOINT = process.env.REACT_APP_RPC_ENDPOINT;
    const WS_ENDPOINT = process.env.REACT_APP_WS_ENDPOINT;

    if (!RPC_ENDPOINT || !WS_ENDPOINT) {
      console.error('RPC_ENDPOINT or WS_ENDPOINT is not defined in .env file.');
      onConnectionStatus('Error');
      return;
    }

    // 创建并存储连接
    const connection = new Connection(RPC_ENDPOINT, {
      commitment: 'confirmed',
      wsEndpoint: WS_ENDPOINT,
    });
    connectionRef.current = connection;

    // 将账户地址转换为 PublicKey 对象
    const publicKey = new PublicKey(account);

    // 设置初始连接状态为 "Connecting"
    onConnectionStatus('Connecting');

    // 订阅账户变化
    const subscribe = async () => {
      try {
        const subscriptionId = await connection.onAccountChange(
          publicKey,
          async (accountInfo, context) => {
            // 处理账户信息更新
            const lamports = accountInfo.lamports;
            console.log('Updated account info:', accountInfo);
            console.log('Slot:', context.slot);

            if (lamports !== undefined) {
              const balance = lamports/1000000000;
              onMessageReceived(`余额: ${balance} $SOL`);
            }

            onConnectionResult(`Slot: ${context.slot}`);

            // 在账户变化时获取所有 SPL Token 余额
            // 在账户变化时获取所有 SPL Token 余额
            const splTokenBalances = await fetchAllSplTokenBalances(connection, account);
            onSPLbalance(`SPL Token 余额: ${JSON.stringify(splTokenBalances)}`);
          },
          'finalized'
        );
        subscriptionIdRef.current = subscriptionId;

        // 订阅成功，更新连接状态为 "Connected"
        onConnectionStatus('Connected');
      } catch (error) {
        console.error('Subscription error:', error);
        onConnectionStatus('Error');
      }
    };

    subscribe();

    // 组件卸载时取消订阅
    return () => {
      if (connectionRef.current && subscriptionIdRef.current !== null) {
        connectionRef.current.removeAccountChangeListener(subscriptionIdRef.current)
          .then(() => {
            console.log('Account change listener removed.');
            onConnectionStatus('Disconnected');
          })
          .catch((error) => {
            console.error('Error removing account change listener:', error);
          });
      }
    };
  }, [onConnectionStatus, account, onMessageReceived, onConnectionResult]);

  return null;
});

export default WebSocketComponent;
