// 导入 React 和必要的钩子函数
import React, { useEffect } from 'react';

// 定义 WebSocketComponent 组件，并使用 React.memo 优化
const WebSocketComponent = React.memo(({ onConnectionStatus, account, onMessageReceived }) => {

  // 使用 useEffect 处理副作用：建立 WebSocket 连接
  useEffect(() => {
    // 创建 WebSocket 连接
    const ws = new WebSocket("wss://api.devnet.solana.com/");

    // WebSocket 连接打开时的处理函数
    const handleOpen = () => {
      console.log("WebSocket connection opened.");
      onConnectionStatus("Connected"); // 通知父组件连接成功
      subscribeToAccount(ws, account); // 订阅账户
    };

    // 接收到消息时的处理函数
    const handleMessage = (ev) => {
      const parsedData = JSON.parse(ev.data.toString()); // 解析接收到的消息
      console.log("Received raw data:", parsedData); // 打印原始数据
      let Receive_data; // 定义变量接收数据

      // 检查所有可能的返回值
      if (parsedData.method) {
        console.log(`Received method: ${parsedData.method}`);
        console.log("lamports: ", parsedData?.params?.result?.value?.lamports);
        Receive_data = parsedData?.params?.result?.value?.lamports;
        if (Receive_data !== undefined) {
          onMessageReceived(`餘額: ${Receive_data}`); // 传递接收到的余额信息
        }
      }
      if (parsedData.result) {
        console.log("Result: ", parsedData.result);
        console.log("lamports: ", parsedData?.params?.result?.value?.lamports);
      }
      if (parsedData.error) {
        console.error("Error: ", parsedData.error);
      }
    };

    // WebSocket 连接关闭时的处理函数
    const handleClose = (event) => {
      console.log("WebSocket connection closed: ", event);
      onConnectionStatus("Disconnected"); // 更新状态为已断开
    };

    // WebSocket 出现错误时的处理函数
    const handleError = (error) => {
      console.error("WebSocket error: ", error);
      onConnectionStatus("Error"); // 更新状态为错误
    };

    // 添加事件监听器
    ws.addEventListener("open", handleOpen);
    ws.addEventListener("message", handleMessage);
    ws.addEventListener("close", handleClose);
    ws.addEventListener("error", handleError);

    // 组件卸载时清理 WebSocket 连接
    return () => {
      ws.close(); // 关闭 WebSocket 连接
    };
  }, [onConnectionStatus, account, onMessageReceived]); // 添加依赖项

  // 订阅账户的函数
  const subscribeToAccount = (ws, account) => {
    const requestData = {
      jsonrpc: "2.0",
      id: 1717,
      method: "accountSubscribe",
      params: [
        account, // 使用传入的账户地址
        {
          encoding: "jsonParsed",
          commitment: "finalized", // 设置数据确认级别
        }, 
      ],
    };
    ws.send(JSON.stringify(requestData)); // 发送订阅请求
  };

  // 该组件不渲染任何 UI
  return null;
});

// 导出 WebSocketComponent 组件
export default WebSocketComponent;
