// 导入 React 及其必要的钩子函数
import React, { useState, useCallback } from 'react';
// 导入样式表
import './App.css';
// 导入自定义的 WebSocket 组件
import WebSocketComponent from './WebSocketComponent';
// 导入 buffer 包并在浏览器环境中 polyfill Buffer
import { Buffer } from 'buffer';
window.Buffer = Buffer;

// 定义主组件 App
const App = () => {
  // 定义状态变量，用于存储连接结果，初始值为 "None"
  const [connectionResult, setConnectionResult] = useState("None");
  // 定义状态变量，用于存储连接状态，初始值为 "Disconnected"
  const [connectionStatus, setConnectionStatus] = useState("Disconnected");
  // 定义状态变量，用于存储接收到的消息，初始为空数组
  const [messages, setMessages] = useState([]);
  // 定义状态变量，用于存储 SPL 代币余额，初始为空数组
  const [splBalances, setSplBalances] = useState([]);
  // 要订阅的账户地址
  const account = "GShiMwUiqpWfxyVxVnYo96upwQekeFVNrtMQT4aSH2RT";

  // 使用 useCallback 定义处理连接状态更新的函数，避免不必要的重新渲染
  const handleConnectionStatus = useCallback((status) => {
    setConnectionStatus(status);
  }, []);

  // 定义处理连接结果更新的函数
  const handleConnectionResult = useCallback((status) => {
    setConnectionResult(status);
  }, []);

  // 定义处理新消息的函数，更新消息列表
  const handleNewMessage = useCallback((message) => {
    setMessages(prevMessages => [...prevMessages, message]);
  }, []);

  // 定义处理 SPL 代币余额更新的函数，解析 JSON 字符串并存储为对象数组
  const handleSPLbalance = useCallback((balances) => {
    setSplBalances(JSON.parse(balances)); // 将 JSON 字符串解析为对象数组
  }, []);

  // 返回要渲染的 JSX 元素
  return (
    <div className="App">
      <header className="App-header">
        {/* 显示 WebSocket 的连接状态 */}
        <h2>WebSocket 狀態: {connectionStatus}</h2>
      </header>
      {/* 引入 WebSocketComponent 组件，并传递必要的属性 */}
      <WebSocketComponent 
        onConnectionStatus={handleConnectionStatus} 
        account={account} 
        onMessageReceived={handleNewMessage} 
        onConnectionResult={handleConnectionResult}
        onSPLbalance={handleSPLbalance}
      />
      {/* 主体内容 */}
      <div className='dashboard-co'>
        {/* 显示账户地址 */}
        <h2>{account}:</h2>
        {/* 显示最新的连接结果 */}
        <h4>最新结果: {connectionResult}</h4>
        <h3>接收到的消息:</h3>
        <div className='dashboard-bottom-container'>
          {/* 左侧消息列表 */}
          <div className='dashboard_left'>
            <ul>
              {messages.map((msg, index) => (
                <li key={index}>{msg}</li> // 显示每条消息
              ))}
            </ul>
          </div>
          {/* 右侧 SPL 代币余额列表 */}
          <div className='dashboard_right'>
            <ul>
              {splBalances.map((token, index) => (
                <li key={index}>
                  Mint: {token.mint} - Balance: {token.balance} // 显示每个代币的 Mint 地址和余额
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

// 导出 App 组件
export default App;
